export async function loadTheme(name) {
  switch (name) {
    case 'dark': {
      return import('./theme-dark.css', { with: { type: 'css' } });
    }
    case 'light': {
      return import('./theme-light.css', { with: { type: 'css' } });
    }
    default: {
      throw Error(`Theme name "${name}" not found`);
    }
  }
}